let weather = {
  apiKey: "YOUR_API_KEY",
  fetchWeather: function (city) {
    fetch(
      "https://api.openweathermap.org/data/2.5/weather?q=" +
        city +
        "&units=metric&appid=" +
        this.apiKey
    )
      .then((response) => {
        if (!response.ok) {
          alert("No weather found.");
          throw new Error("No weather found.");
        }
        return response.json();
      })
      .then((data) => this.displayWeather(data));
  },
  displayWeather: function (data) {
    const { name } = data;
    const { icon, description } = data.weather[0];
    const { temp, humidity } = data.main;
    const { speed } = data.wind;
    document.querySelector(".city").innerText = "Weather in " + name;
    document.querySelector(".icon").src =
      "https://openweathermap.org/img/wn/" + icon + ".png";
    document.querySelector(".description").innerText = description;
    document.querySelector(".temp").innerText = temp + "Â°C";
    document.querySelector(".humidity").innerText =
      "Humidity: " + humidity + "%";
    document.querySelector(".wind").innerText =
      "Wind speed: " + speed + " km/h";
    document.querySelector(".weather").classList.remove("loading");
    document.body.style.backgroundImage =
      "url('https://source.unsplash.com/1920x1080/?" + name + "')";
  },
  search: function () {
    this.fetchWeather(document.querySelector(".search-bar").value);
  },
};

let geocode = {
  reverseGeocode: function (latitude, longitude) {
    var apikey = "YOUR_API_KEY";

    var api_url = "https://api.opencagedata.com/geocode/v1/json";

    var request_url =
      api_url +
      "?" +
      "key=" +
      apikey +
      "&q=" +
      encodeURIComponent(latitude + "," + longitude) +
      "&pretty=1" +
      "&no_annotations=1";

    // see full list of required and optional parameters:
    // https://opencagedata.com/api#forward

    var request = new XMLHttpRequest();
    request.open("GET", request_url, true);

    request.onload = function () {
      // see full list of possible response codes:
      // https://opencagedata.com/api#codes

      if (request.status == 200) {
        // Success!
        var data = JSON.parse(request.responseText);
        weather.fetchWeather(data.results[0].components.country);
      } else if (request.status <= 500) {
        // We reached our target server, but it returned an error

        console.error("Unable to Geocode! Response code: " + request.status);
        var data = JSON.parse(request.responseText);
        console.error("Error msg: " + data.status.message);
      } else {
        console.error("Server error!");
      }
    };

    request.onerror = function () {
      // There was a connection error of some sort
      console.error("Unable to connect to server!");
    };

    request.send(); // make the request
  },
  getLocation: function() {
    function success (data) {
      geocode.reverseGeocode(data.coords.latitude, data.coords.longitude);
    }
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(success, showError);
    } else {
      randomWeather()
    }
  }
};

function showError(error) {
  randomWeather()
  switch(error.code) {
    case error.PERMISSION_DENIED:
      console.error("User denied access to location.")
      alert("User denied access to location.")
      break;
    case error.POSITION_UNAVAILABLE:
      console.error("Location information is unavailable.")
      alert("Location information is unavailable.")
      break;
    case error.TIMEOUT:
      console.error("User location request timed out.")
      alert("User location request timed out.")
      break;
    case error.UNKNOWN_ERROR:
      console.error("An unknown error occurred.")
      alert("An unknown error occurred.")
      break;
  }
}

